export const addUserName = (newUserName) => ({
    type: "ADD_USER_NAME",
    name: newUserName
})

export const addUserEmail = (newUserEmail) => ({
    type: "ADD_USER_EMAIL",
    email: newUserEmail
})

export const addUserPassword = (newUserPassword) => ({
    type: "ADD_USER_PASSWORD",
    password: newUserPassword
})